let props = defineProps({
  width: {
    type: [Number, String],
    default: null,
  },
  height: {
    type: [Number, String],
    default: null,
  },
  ratio: {
    type: Number,
    default: 1,
  },
  color: {
    type: Object,
    default() {
      return {
        nuxtHillRight: "fill-[#047857]",
        nuxtHillLeft: "fill-[#10b881]",
        tailwindCloudTop: "fill-[#165e75]",
        tailwindCloudBottom: "fill-[#0891b1]",
      };
    },
  },
});

let svgHeight = computed(() => {
  if (props.height && typeof props.height === "string") {
    return props.height;
  }
  if (props.height && props.height > 0) {
    return props.height;
  }
  if (props.width && props.width > 0) {
    return props.width * props.ratio;
  }
  return null;
});

let svgWidth = computed(() => {
  if (props.width && typeof props.width === "string") {
    return props.width;
  }
  if (props.width && props.width > 0) {
    return props.width;
  }
  if (props.height && props.height > 0) {
    return props.height * props.ratio;
  }
  return null;
});
</script>
